(72, 'Paličkování v DDM v Karlových Varech', '<p><strong>29. května 2010 Domov dětí a mládeže na Čankovské v Karlových Varech</strong></p>\r\n<p> </p>\r\n<p>Od 10:00 hod pravidelné sobotní setkání krajkářek</p>', '2010-05-29 00:00:00', NULL, NULL),
(89, 'Vamberk 2010', '<p><strong>XII. Mezinárodní setkání krajkářek</strong></p>\r\n<p> </p>\r\n<p>25. - 27. 6. 2010</p>\r\n<p><strong>V. Bienále české krajky</strong></p>\r\n<p>Španělské krajky ze sbírek Marini Regueiro</p>\r\n<p>Stálá expozice "Tři století české krajky"</p>\r\n<p><strong>Velká módní přehlídka</strong></p>\r\n<p>Výstavy:</p>\r\n<p>Německý krajkářský svaz</p>\r\n<p>Krajkářská škola Vamberk</p>', '2010-06-25 00:00:00', 'f474b2cc62d445_sm.jpg', 'f474b2cc62d445.jpg'),
