INSERT INTO `Internals` (`id_internal`, `nazev`, `popis`, `datum`) VALUES
(20, 'Pravidelné schůzky krajkářek v DDM na Čankovské', '<p>V DDM paličkujeme každé pondělí od 17. 00 do cca 20,30 hod. <strong>Začínáme 7. 9. 2009.</strong></p>\r\n<p> </p>\r\n<p>Jitka</p>', '2009-09-03 16:12:41'),
(34, 'Ostružná 2010', '<p>Milé krajkářky,</p>\r\n<p> </p>\r\n<p>kdo má zájem o Ostružnou 2010, at se přihlásí buď u Marušky Vránové nebo u Boženky Havlíčkové.</p>\r\n<p>Termín dovolené je od 7.8.2010, cena zůstává stejná 2.520 k.</p>\r\n<p> </p>\r\n<p>díky Boženka</p>', '2010-04-12 09:14:27'),
(36, 'Výstava ručně šitých hraček v Ostrově', '<p>Milé šikulky,</p>\r\n<p> </p>\r\n<p>moc bych potřebovala na léto (červenec, srpen) na Starou radnici v Ostrově zapůjčit panenky, které jsou nyní v Kraslicích a ručně šité hračky - ponožková zvířátka, postavičky, pokud máte i nějaké staré obrázky holčiček s panenkami, kočárky apod. Mám udělat výstavu pro děti a bude to celé prázdiny. Mám s tím docela dost práce.</p>\r\n<p> </p>\r\n<p>Děkuji předem Jana Dvořáková</p>\r\n<p> </p>\r\n<p> </p>', '2010-04-28 17:19:43'),
(37, 'Zájezd do Prachatic 8.6. 2010', '<p><strong>Zájezd do Prachatic      úterý 8. 6. 2010</strong></p>\r\n<p><strong><br /></strong></p>\r\n<p>5,30 odjezd autobusu z Aše (autobusová zastávka Liďák)</p>\r\n<p>6,00 odjezd z Chebu (bývalá Elektra u nádraží ČD)</p>\r\n<p>Sokolov (pokud někdo pojede, upřesníme)</p>\r\n<p>7,00 odjezd z Karlových Varů (od horního nádraží), sraz před nádražím v 6,50</p>\r\n<p><br />asi 10,30 - 11,00 příjezd do Sedlice, navštívíme prodejnu Sedlické krajky (krajkářská škola v současné době nemá vlastní prostory, učí se v budově ZŠ)</p>\r\n<p>asi 11,30 - 12,00 odjezd do Prachatic<br />14,00 - 14,45 prohlídka Muzea krajky s průvodcem - 1. skupina<br />15,00 - 15,45 prohlídka Muzea krajky s průvodcem - 2. skupina</p>\r\n<p> </p>\r\n<p>16,00 odjezd z Prachatic</p>\r\n<p> </p>\r\n<p>kolem 20. hodiny příjezd do Karlových Varů<br />do 22. hodiny příjezd do Aše</p>\r\n<p> </p>\r\n<p><strong>Cena zájezdu: pro členky OSKK = 300 Kč </strong>(cesta a vstup do muzea)<br /> pro nečleny = 600 Kč (cesta a vstup do muzea)</p>\r\n<p> </p>\r\n<p>Zaplatit můžete M. Vránové každé úterý na paličkování, v sobotu 29.5. na setkání v DDM v Karlových Varech, a ti, kteří pojedou jen na zájezd, tak při nástupu do autobusu.</p>\r\n<p> </p>\r\n<p><strong>Vlak</strong>: Nejdek 6,20 - K.V. 6,47            K.V. 19,11 nebo 21,03 - Nejdek 19,43 nebo 21,35<br /> Chomutov 5,29 - K.V. 6,29       K.V. 19, 06 nebo 20,23 - Chomutov  19,58 nebo 21,29</p>\r\n<p> </p>', '2010-05-25 16:34:05'),
(30, 'Pravidelné schůzky krajkářek v Chebu', '<p>Každé pondělí paličkujeme v místnostech Klubu důchodců od 14:00 do 16:30 hodin. Od května i ve čtvrtek ve stejnou dobu.</p>\r\n<p>Pravidelně chodí 17 paličkářek .</p>', '2010-04-26 10:31:12'),
(18, 'Pravidelné schůzky krajkářek v Sokolově', '<p>V Sokolově DDM začínáme paličkovat od <strong>pondělí 7. září 2009</strong> - a pak každé pondělí 16 - 19 hodin.</p>\r\n<p>Líba Šťovíkoví</p>', '2009-09-02 16:35:22'),
(19, 'Pravidelné schůzky krajkářek v Karlových Varech', '<p>Paličkování v ZŠ Poštovní 19, v Karlových Varech - Tuhnicích začíná v úterý <strong>8. září 2009.</strong><br />Pravidelná setkávání budou nadále v úterý od 14,30 do 18 hodin.</p>\r\n<p>Marie Vránová.</p>', '2009-09-02 16:34:37');
